import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import './Practice.css';

const Practice = ({ words, onWordUpdated }) => {
  const [currentWordIndex, setCurrentWordIndex] = useState(0);
  const [showAnswer, setShowAnswer] = useState(false);
  const [practiceWords, setPracticeWords] = useState([]);
  const [practiceMode, setPracticeMode] = useState('all');
  const [sentences, setSentences] = useState([]);
  const [loadingSentences, setLoadingSentences] = useState(false);

  const filterWordsForPractice = useCallback(() => {
    let filtered = [];
    switch (practiceMode) {
      case 'new':
        filtered = words.filter(word => word.userLevel === 0);
        break;
      case 'learning':
        filtered = words.filter(word => word.userLevel > 0 && word.userLevel < 5);
        break;
      case 'review':
        filtered = words.filter(word => word.userLevel > 0);
        break;
      default:
        filtered = words;
    }
    setPracticeWords(filtered);
    setCurrentWordIndex(0);
    setShowAnswer(false);
  }, [words, practiceMode]);

  useEffect(() => {
    filterWordsForPractice();
  }, [filterWordsForPractice]);

  const currentWord = practiceWords[currentWordIndex];

  const handleNext = () => {
    if (currentWordIndex < practiceWords.length - 1) {
      setCurrentWordIndex(currentWordIndex + 1);
      setShowAnswer(false);
    }
  };

  const handlePrevious = () => {
    if (currentWordIndex > 0) {
      setCurrentWordIndex(currentWordIndex - 1);
      setShowAnswer(false);
    }
  };

  const handleProgressUpdate = async (newLevel) => {
    if (!currentWord) return;
    
    try {
      const response = await axios.post(`/api/words/${currentWord.id}/progress`, {
        userLevel: newLevel
      });
      onWordUpdated(response.data);
    } catch (error) {
      console.error('Error updating progress:', error);
    }
  };

  const generateSentences = async () => {
    if (!currentWord) return;
    
    setLoadingSentences(true);
    try {
      const response = await axios.post('/api/ai/generate-sentences', {
        words: [currentWord.english],
        count: 3
      });
      setSentences(response.data.sentences);
    } catch (error) {
      console.error('Error generating sentences:', error);
      alert('Failed to generate sentences');
    } finally {
      setLoadingSentences(false);
    }
  };

  const getLevelColor = (level) => {
    const colors = ['#dc3545', '#fd7e14', '#ffc107', '#20c997', '#198754', '#0d6efd'];
    return colors[Math.min(level, 5)];
  };

  if (practiceWords.length === 0) {
    return (
      <div className="practice">
        <div className="practice-header">
          <h2>Practice Mode</h2>
          <div className="practice-controls">
            <select 
              value={practiceMode} 
              onChange={(e) => setPracticeMode(e.target.value)}
              className="practice-mode-select"
            >
              <option value="all">All Words</option>
              <option value="new">New Words (Level 0)</option>
              <option value="learning">Learning (Level 1-4)</option>
              <option value="review">Review (Level 1-5)</option>
            </select>
          </div>
        </div>
        
        <div className="no-words-message">
          <p>No words available for the selected practice mode.</p>
          <p>Try adding some words or changing the practice mode.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="practice">
      <div className="practice-header">
        <h2>Practice Mode</h2>
        <div className="practice-controls">
          <select 
            value={practiceMode} 
            onChange={(e) => setPracticeMode(e.target.value)}
            className="practice-mode-select"
          >
            <option value="all">All Words</option>
            <option value="new">New Words (Level 0)</option>
            <option value="learning">Learning (Level 1-4)</option>
            <option value="review">Review (Level 1-5)</option>
          </select>
        </div>
      </div>

      <div className="practice-info">
        <p>Word {currentWordIndex + 1} of {practiceWords.length}</p>
        <p>Practice Mode: {practiceMode}</p>
      </div>

      <div className="flashcard">
        <div className="flashcard-content">
          <div className="word-display">
            <div className="english-word">{currentWord.english}</div>
            {showAnswer && (
              <div className="hebrew-word">{currentWord.hebrew}</div>
            )}
          </div>
          
          <div className="word-meta">
            <span 
              className="difficulty-badge"
              style={{backgroundColor: getLevelColor(currentWord.userLevel)}}
            >
              Level: {currentWord.userLevel}
            </span>
            <span className="difficulty-info">
              Difficulty: {currentWord.difficulty}
            </span>
          </div>
        </div>

        <div className="flashcard-actions">
          <button 
            className="btn btn-primary"
            onClick={() => setShowAnswer(!showAnswer)}
          >
            {showAnswer ? 'Hide Answer' : 'Show Answer'}
          </button>
        </div>

        {showAnswer && (
          <div className="progress-controls">
            <h3>Update Your Progress:</h3>
            <div className="progress-buttons">
              <button 
                className="btn btn-sm"
                onClick={() => handleProgressUpdate(Math.max(0, currentWord.userLevel - 1))}
                disabled={currentWord.userLevel === 0}
              >
                Decrease Level
              </button>
              <span className="current-level">Level {currentWord.userLevel}</span>
              <button 
                className="btn btn-sm"
                onClick={() => handleProgressUpdate(Math.min(5, currentWord.userLevel + 1))}
                disabled={currentWord.userLevel === 5}
              >
                Increase Level
              </button>
            </div>
          </div>
        )}

        {showAnswer && (
          <div className="sentences-section">
            <button 
              className="btn btn-secondary"
              onClick={generateSentences}
              disabled={loadingSentences}
            >
              {loadingSentences ? 'Generating...' : 'Generate Example Sentences'}
            </button>
            
            {sentences.length > 0 && (
              <div className="sentences">
                <h3>Example Sentences:</h3>
                <ul>
                  {sentences.map((sentence, index) => (
                    <li key={index}>{sentence}</li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        )}
      </div>

      <div className="navigation-controls">
        <button 
          className="btn btn-secondary"
          onClick={handlePrevious}
          disabled={currentWordIndex === 0}
        >
          Previous
        </button>
        
        <span className="word-counter">
          {currentWordIndex + 1} / {practiceWords.length}
        </span>
        
        <button 
          className="btn btn-secondary"
          onClick={handleNext}
          disabled={currentWordIndex === practiceWords.length - 1}
        >
          Next
        </button>
      </div>

      <div className="practice-stats">
        <h3>Practice Statistics</h3>
        <div className="stats-grid">
          <div className="stat-item">
            <span className="stat-label">Total Words:</span>
            <span className="stat-value">{practiceWords.length}</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">New (Level 0):</span>
            <span className="stat-value">{practiceWords.filter(w => w.userLevel === 0).length}</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Learning (1-4):</span>
            <span className="stat-value">{practiceWords.filter(w => w.userLevel > 0 && w.userLevel < 5).length}</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Mastered (Level 5):</span>
            <span className="stat-value">{practiceWords.filter(w => w.userLevel === 5).length}</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Practice;
