import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import axios from 'axios';
import WordList from './components/WordList';
import AddWord from './components/AddWord';
import Practice from './components/Practice';
import Statistics from './components/Statistics';
import './App.css';

function App() {
  const [words, setWords] = useState([]);
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState(null);

  useEffect(() => {
    fetchWords();
    fetchStats();
  }, []);

  const fetchWords = async () => {
    try {
      const response = await axios.get('/api/words');
      setWords(response.data);
    } catch (error) {
      console.error('Error fetching words:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchStats = async () => {
    try {
      const response = await axios.get('/api/words/progress/stats');
      setStats(response.data);
    } catch (error) {
      console.error('Error fetching stats:', error);
    }
  };

  const handleWordAdded = (newWord) => {
    setWords([...words, newWord]);
    fetchStats();
  };

  const handleWordUpdated = (updatedWord) => {
    setWords(words.map(word => word.id === updatedWord.id ? updatedWord : word));
    fetchStats();
  };

  const handleWordDeleted = (wordId) => {
    setWords(words.filter(word => word.id !== wordId));
    fetchStats();
  };

  if (loading) {
    return (
      <div className="container">
        <div className="loading">Loading...</div>
      </div>
    );
  }

  return (
    <Router>
      <div className="App">
        <nav className="navbar">
          <div className="container">
            <h1 className="nav-title">Word Memory App</h1>
            <div className="nav-links">
              <Link to="/" className="nav-link">Words</Link>
              <Link to="/add" className="nav-link">Add Word</Link>
              <Link to="/practice" className="nav-link">Practice</Link>
              <Link to="/stats" className="nav-link">Statistics</Link>
            </div>
          </div>
        </nav>

        <main className="main-content">
          <div className="container">
            <Routes>
              <Route 
                path="/" 
                element={
                  <WordList 
                    words={words} 
                    onWordUpdated={handleWordUpdated}
                    onWordDeleted={handleWordDeleted}
                  />
                } 
              />
              <Route 
                path="/add" 
                element={
                  <AddWord 
                    onWordAdded={handleWordAdded}
                  />
                } 
              />
              <Route 
                path="/practice" 
                element={
                  <Practice 
                    words={words}
                    onWordUpdated={handleWordUpdated}
                  />
                } 
              />
              <Route 
                path="/stats" 
                element={
                  <Statistics 
                    stats={stats}
                    words={words}
                  />
                } 
              />
            </Routes>
          </div>
        </main>
      </div>
    </Router>
  );
}

export default App;
