import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Practice.css';

const Practice = ({ words, onWordUpdated }) => {
  const [currentMode, setCurrentMode] = useState(null); // null = main menu, 'knowledge', 'flashcards'

  const handleModeSelect = (mode) => {
    setCurrentMode(mode);
  };

  const handleExit = () => {
    setCurrentMode(null);
  };

  // Main menu
  if (currentMode === null) {
    return (
      <div className="practice-main-menu">
        <div className="practice-menu-container">
          <h1>Practice Mode</h1>
          <div className="practice-options">
            <button 
              className="practice-option knowledge-check"
              onClick={() => handleModeSelect('knowledge')}
            >
              <div className="option-icon">üìä</div>
              <div className="option-content">
                <h3>Knowledge Check</h3>
                <p>Test your knowledge level with interactive quizzes</p>
              </div>
            </button>

            <button 
              className="practice-option flashcards"
              onClick={() => handleModeSelect('flashcards')}
            >
              <div className="option-icon">üÉè</div>
              <div className="option-content">
                <h3>Flash Cards</h3>
                <p>Traditional flash card practice mode</p>
              </div>
            </button>
          </div>
        </div>
      </div>
    );
  }

  // Knowledge Check Mode
  if (currentMode === 'knowledge') {
    return <KnowledgeCheck words={words} onWordUpdated={onWordUpdated} onExit={handleExit} />;
  }

  // Flash Cards Mode
  if (currentMode === 'flashcards') {
    return <FlashCards words={words} onWordUpdated={onWordUpdated} onExit={handleExit} />;
  }

  return null;
};

// Knowledge Check Component with Kahoot-style design and learning algorithm
const KnowledgeCheck = ({ words, onWordUpdated, onExit }) => {
  const [currentWordIndex, setCurrentWordIndex] = useState(0);
  const [showHebrew, setShowHebrew] = useState(false);
  const [selectedLevel, setSelectedLevel] = useState(null);
  const [practiceWords, setPracticeWords] = useState([]);
  const [isUpdating, setIsUpdating] = useState(false);
  const [localWords, setLocalWords] = useState([]);
  const [speechSupported, setSpeechSupported] = useState(false);
  const [sessionStats, setSessionStats] = useState({ correct: 0, total: 0 });
  const [showResult, setShowResult] = useState(false);
  const [answeredWord, setAnsweredWord] = useState(null);

  useEffect(() => {
    // Create a local copy of words to prevent issues
    setLocalWords([...words]);

    // Check if speech synthesis is supported
    if ('speechSynthesis' in window) {
      setSpeechSupported(true);
    }
  }, [words]);

  // Smart Learning Algorithm - prioritizes words with lower knowledge levels
  const createSmartPracticeSet = (words) => {
    // Filter words that are not mastered (level < 5)
    const availableWords = words.filter(word => word.userLevel < 5);

    if (availableWords.length === 0) return [];

    // Weight words based on their knowledge level (lower level = higher weight)
    const weightedWords = [];

    availableWords.forEach(word => {
      // Level 0: appears 5 times, Level 1: 4 times, Level 2: 3 times, etc.
      const weight = Math.max(1, 5 - word.userLevel);
      for (let i = 0; i < weight; i++) {
        weightedWords.push(word);
      }
    });

    // Shuffle the weighted array for randomized but prioritized selection
    const shuffled = [...weightedWords].sort(() => Math.random() - 0.5);

    // Take a subset for the session (max 20 words to keep sessions manageable)
    const sessionSize = Math.min(20, shuffled.length);
    return shuffled.slice(0, sessionSize);
  };

  useEffect(() => {
    // Create smart practice set using the learning algorithm
    const smartPracticeSet = createSmartPracticeSet(localWords);
    setPracticeWords(smartPracticeSet);

    // Reset state when words change
    if (smartPracticeSet.length === 0) {
      setCurrentWordIndex(0);
      setShowHebrew(false);
      setSelectedLevel(null);
    } else if (currentWordIndex >= smartPracticeSet.length) {
      // If current index is out of bounds, reset to 0
      setCurrentWordIndex(0);
      setShowHebrew(false);
      setSelectedLevel(null);
    }
  }, [localWords, currentWordIndex]);

  const currentWord = practiceWords[currentWordIndex];

  // Manual speech synthesis function
  const speakWord = (word) => {
    if (!speechSupported || !word) return;

    // Stop any current speech
    window.speechSynthesis.cancel();

    const utterance = new SpeechSynthesisUtterance(word);
    utterance.lang = 'en-US';
    utterance.rate = 0.8;
    utterance.pitch = 1;
    utterance.volume = 1;

    window.speechSynthesis.speak(utterance);
  };

  const handleLevelSelect = async (level) => {
    if (!currentWord || isUpdating) {
      return;
    }

    setIsUpdating(true);
    setSelectedLevel(level);
    setAnsweredWord(currentWord); // Store the word that was just answered
    setShowResult(true);

    // Update session stats (consider level 3+ as "correct")
    const isCorrect = level >= 3;
    setSessionStats(prev => ({
      correct: prev.correct + (isCorrect ? 1 : 0),
      total: prev.total + 1
    }));

    try {
      const response = await axios.post(`/api/words/${currentWord.id}/progress`, {
        userLevel: level
      });

      // Update the word in the parent component
      onWordUpdated(response.data);

      // For level 5, we don't immediately remove the word from local list
      // Instead, we update it and let the useEffect handle the filtering
      setLocalWords(prev => 
        prev.map(word => 
          word.id === currentWord.id 
            ? { ...word, userLevel: level }
            : word
        )
      );

      // Don't auto-advance, wait for user to click "Next"
      setIsUpdating(false);
    } catch (error) {
      console.error('Error updating progress:', error);
      setSelectedLevel(null);
      setIsUpdating(false);
      alert('Failed to update progress. Please try again.');
    }
  };

  const handleNextWord = () => {
    if (currentWordIndex < practiceWords.length - 1) {
      setCurrentWordIndex(prev => prev + 1);
      setShowHebrew(false);
      setSelectedLevel(null);
      setShowResult(false);
      setAnsweredWord(null);
    } else {
      // Session completed
      const accuracy = Math.round((sessionStats.correct / sessionStats.total) * 100);
      alert(`Knowledge check completed!\nAccuracy: ${accuracy}% (${sessionStats.correct}/${sessionStats.total})`);
      onExit();
    }
  };

  const getLevelColor = (level) => {
    const colors = ['#dc3545', '#fd7e14', '#ffc107', '#20c997', '#198754', '#0d6efd'];
    return colors[Math.min(level, 5)];
  };

  if (practiceWords.length === 0) {
    return (
      <div className="practice-fullscreen">
        <button className="exit-button" onClick={onExit}>‚úï</button>
        <div className="practice-content">
          <div className="no-words">
            <h2>No words available for practice</h2>
            <p>All your words are already mastered (Level 5) or you need to add more words.</p>
            <button onClick={onExit}>Back to Menu</button>
          </div>
        </div>
      </div>
    );
  }

  if (!currentWord) {
    return (
      <div className="practice-fullscreen">
        <button className="exit-button" onClick={onExit}>‚úï</button>
        <div className="practice-content">
          <div className="no-words">
            <h2>No words available for practice</h2>
            <button onClick={onExit}>Back to Menu</button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="kahoot-fullscreen">
      <button className="kahoot-exit-btn" onClick={onExit}>‚úï</button>

      {/* Header with progress and stats */}
      <div className="kahoot-header">
        <div className="kahoot-progress">
          <div className="progress-bar">
            <div 
              className="progress-fill"
              style={{ width: `${((currentWordIndex + 1) / practiceWords.length) * 100}%` }}
            ></div>
          </div>
          <span className="progress-text">
            {currentWordIndex + 1} / {practiceWords.length}
          </span>
        </div>
        <div className="session-stats">
          <span className="stat">‚úÖ {sessionStats.correct}</span>
          <span className="stat">üìä {sessionStats.total}</span>
        </div>
      </div>

      {/* Main content area */}
      <div className="kahoot-content">
        {!showResult ? (
          <>
            {/* Question area */}
            <div className="kahoot-question">
              <h1 className="english-word">{currentWord.english}</h1>

              {speechSupported && (
                <button 
                  className="kahoot-audio-btn"
                  onClick={() => speakWord(currentWord.english)}
                  title="Play pronunciation"
                >
                  üîä Play Audio
                </button>
              )}

              {showHebrew && (
                <div className="hebrew-reveal">
                  <h2 className="hebrew-word">{currentWord.hebrew}</h2>
                </div>
              )}
            </div>

            {/* Answer buttons - Kahoot style */}
            <div className="kahoot-answers">
              <h3 className="question-text">How well do you know this word?</h3>

              <div className="answer-grid">
                <button
                  className="answer-btn red"
                  onClick={() => handleLevelSelect(0)}
                  disabled={isUpdating}
                >
                  <div className="answer-shape triangle"></div>
                  <span>Don't Know</span>
                  <small>Level 0</small>
                </button>

                <button
                  className="answer-btn blue"
                  onClick={() => handleLevelSelect(1)}
                  disabled={isUpdating}
                >
                  <div className="answer-shape diamond"></div>
                  <span>Beginner</span>
                  <small>Level 1</small>
                </button>

                <button
                  className="answer-btn yellow"
                  onClick={() => handleLevelSelect(2)}
                  disabled={isUpdating}
                >
                  <div className="answer-shape circle"></div>
                  <span>Basic</span>
                  <small>Level 2</small>
                </button>

                <button
                  className="answer-btn green"
                  onClick={() => handleLevelSelect(3)}
                  disabled={isUpdating}
                >
                  <div className="answer-shape square"></div>
                  <span>Good</span>
                  <small>Level 3</small>
                </button>
              </div>

              <div className="advanced-options">
                <button
                  className="advanced-btn purple"
                  onClick={() => handleLevelSelect(4)}
                  disabled={isUpdating}
                >
                  Advanced (4)
                </button>

                <button
                  className="advanced-btn gold"
                  onClick={() => handleLevelSelect(5)}
                  disabled={isUpdating}
                >
                  Mastered (5)
                </button>
              </div>
            </div>

            {/* Show Hebrew button */}
            <div className="kahoot-actions">
              <button 
                className="show-hebrew-btn"
                onClick={() => setShowHebrew(!showHebrew)}
                disabled={isUpdating}
              >
                {showHebrew ? 'üôà Hide Hebrew' : 'üëÅÔ∏è Show Hebrew'}
              </button>
            </div>
          </>
        ) : (
          /* Result screen */
          <div className="kahoot-result">
            <div className="result-icon">
              {selectedLevel >= 3 ? 'üéâ' : 'üí™'}
            </div>
            <h2>
              {selectedLevel >= 3 ? 'Great job!' : 'Keep practicing!'}
            </h2>
            {answeredWord && (
              <div className="result-details">
                <div className="word-pair">
                  <span className="english">{answeredWord.english}</span>
                  <span className="hebrew">{answeredWord.hebrew}</span>
                </div>
                <div className="level-result">
                  You rated this as Level {selectedLevel}
                </div>
              </div>
            )}

            <button 
              className="kahoot-next-btn"
              onClick={handleNextWord}
            >
              {currentWordIndex < practiceWords.length - 1 ? 'Next Word ‚Üí' : 'Finish Session'}
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

// Flash Cards Component
const FlashCards = ({ words, onWordUpdated, onExit }) => {
  const [currentWordIndex, setCurrentWordIndex] = useState(0);
  const [showAnswer, setShowAnswer] = useState(false);
  const [practiceWords, setPracticeWords] = useState([]);
  const [practiceMode, setPracticeMode] = useState('all');

  useEffect(() => {
    let filtered = [];
    switch (practiceMode) {
      case 'new':
        filtered = words.filter(word => word.userLevel === 0);
        break;
      case 'learning':
        filtered = words.filter(word => word.userLevel > 0 && word.userLevel < 5);
        break;
      case 'review':
        filtered = words.filter(word => word.userLevel > 0);
        break;
      default:
        filtered = words;
    }
    setPracticeWords(filtered);
    setCurrentWordIndex(0);
    setShowAnswer(false);
  }, [words, practiceMode]);

  const currentWord = practiceWords[currentWordIndex];

  const handleNext = () => {
    if (currentWordIndex < practiceWords.length - 1) {
      setCurrentWordIndex(currentWordIndex + 1);
      setShowAnswer(false);
    }
  };

  const handlePrevious = () => {
    if (currentWordIndex > 0) {
      setCurrentWordIndex(currentWordIndex - 1);
      setShowAnswer(false);
    }
  };

  const handleProgressUpdate = async (newLevel) => {
    if (!currentWord) return;

    try {
      const response = await axios.post(`/api/words/${currentWord.id}/progress`, {
        userLevel: newLevel
      });
      onWordUpdated(response.data);
    } catch (error) {
      console.error('Error updating progress:', error);
    }
  };

  const getLevelColor = (level) => {
    const colors = ['#dc3545', '#fd7e14', '#ffc107', '#20c997', '#198754', '#0d6efd'];
    return colors[Math.min(level, 5)];
  };

  if (practiceWords.length === 0) {
    return (
      <div className="practice-fullscreen">
        <button className="exit-button" onClick={onExit}>‚úï</button>
        <div className="practice-content">
          <div className="no-words">
            <h2>No words available for practice</h2>
            <button onClick={onExit}>Back to Menu</button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="practice-fullscreen">
      <button className="exit-button" onClick={onExit}>‚úï</button>
      <div className="practice-content">
        <div className="flashcard-container">
          <div className="flashcard-header">
            <select 
              value={practiceMode} 
              onChange={(e) => setPracticeMode(e.target.value)}
              className="practice-mode-select"
            >
              <option value="all">All Words</option>
              <option value="new">New Words (Level 0)</option>
              <option value="learning">Learning (Level 1-4)</option>
              <option value="review">Review (Level 1-5)</option>
            </select>

            <div className="flashcard-progress">
              {currentWordIndex + 1} / {practiceWords.length}
            </div>
          </div>

          <div className="flashcard">
            <div className="flashcard-content">
              <h2 className="flashcard-word">{currentWord.english}</h2>
              {showAnswer && (
                <div className="flashcard-answer">
                  <h3>{currentWord.hebrew}</h3>
                  <div className="word-meta">
                    <span 
                      className="difficulty-badge"
                      style={{backgroundColor: getLevelColor(currentWord.difficulty)}}
                    >
                      Difficulty: {currentWord.difficulty}
                    </span>
                    <span 
                      className="level-badge"
                      style={{backgroundColor: getLevelColor(currentWord.userLevel)}}
                    >
                      Your Level: {currentWord.userLevel}
                    </span>
                  </div>
                </div>
              )}
            </div>
          </div>

          <div className="flashcard-controls">
            <button onClick={handlePrevious} disabled={currentWordIndex === 0}>
              ‚Üê Previous
            </button>

            <button 
              className="show-answer-btn"
              onClick={() => setShowAnswer(!showAnswer)}
            >
              {showAnswer ? 'Hide Answer' : 'Show Answer'}
            </button>

            <button onClick={handleNext} disabled={currentWordIndex === practiceWords.length - 1}>
              Next ‚Üí
            </button>
          </div>

          {showAnswer && (
            <div className="progress-controls">
              <button 
                onClick={() => handleProgressUpdate(Math.max(0, currentWord.userLevel - 1))}
                disabled={currentWord.userLevel === 0}
              >
                Decrease Level
              </button>
              <span>Current Level: {currentWord.userLevel}</span>
              <button 
                onClick={() => handleProgressUpdate(Math.min(5, currentWord.userLevel + 1))}
                disabled={currentWord.userLevel === 5}
              >
                Increase Level
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Practice;
