import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Settings.css';

const Settings = () => {
  const [settings, setSettings] = useState({
    maxNewWords: 5,
    maxReviewWords: 20,
    focusPeriodDays: 5,
    sessionSize: 10,
    autoAdvance: true,
    showKeyboardHints: true
  });
  
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(false);
  const [saved, setSaved] = useState(false);

  useEffect(() => {
    loadSettings();
    loadStats();
  }, []);

  const loadSettings = () => {
    const savedSettings = localStorage.getItem('wordGameSettings');
    if (savedSettings) {
      setSettings({ ...settings, ...JSON.parse(savedSettings) });
    }
  };

  const loadStats = async () => {
    try {
      const response = await axios.get('/api/words/stats/learning');
      setStats(response.data);
    } catch (error) {
      console.error('Error loading stats:', error);
    }
  };

  const saveSettings = () => {
    setLoading(true);
    localStorage.setItem('wordGameSettings', JSON.stringify(settings));
    
    setTimeout(() => {
      setLoading(false);
      setSaved(true);
      setTimeout(() => setSaved(false), 2000);
    }, 500);
  };

  const handleSettingChange = (key, value) => {
    setSettings(prev => ({
      ...prev,
      [key]: value
    }));
  };

  const resetToDefaults = () => {
    const defaultSettings = {
      maxNewWords: 5,
      maxReviewWords: 20,
      focusPeriodDays: 5,
      sessionSize: 10,
      autoAdvance: true,
      showKeyboardHints: true
    };
    setSettings(defaultSettings);
  };

  return (
    <div className="settings-container">
      <div className="settings-header">
        <h1>⚙️ Learning Settings</h1>
        <p>Customize your spaced repetition learning experience</p>
      </div>

      {/* Learning Statistics */}
      {stats && (
        <div className="stats-section">
          <h2>📊 Learning Progress</h2>
          <div className="stats-grid">
            <div className="stat-card">
              <div className="stat-number">{stats.totalWords}</div>
              <div className="stat-label">Total Words</div>
            </div>
            <div className="stat-card">
              <div className="stat-number">{stats.newWords}</div>
              <div className="stat-label">New Words</div>
            </div>
            <div className="stat-card">
              <div className="stat-number">{stats.learningWords}</div>
              <div className="stat-label">Learning</div>
            </div>
            <div className="stat-card">
              <div className="stat-number">{stats.masteredWords}</div>
              <div className="stat-label">Mastered</div>
            </div>
            <div className="stat-card">
              <div className="stat-number">{stats.dueToday}</div>
              <div className="stat-label">Due Today</div>
            </div>
            <div className="stat-card">
              <div className="stat-number">{stats.inFocus}</div>
              <div className="stat-label">In Focus</div>
            </div>
          </div>
        </div>
      )}

      {/* Spaced Repetition Settings */}
      <div className="settings-section">
        <h2>🧠 Spaced Repetition Algorithm</h2>
        
        <div className="setting-group">
          <label className="setting-label">
            <span>New Words Per Day</span>
            <span className="setting-description">Maximum new words to introduce daily</span>
          </label>
          <div className="setting-control">
            <input
              type="range"
              min="1"
              max="20"
              value={settings.maxNewWords}
              onChange={(e) => handleSettingChange('maxNewWords', parseInt(e.target.value))}
              className="setting-slider"
            />
            <span className="setting-value">{settings.maxNewWords}</span>
          </div>
        </div>

        <div className="setting-group">
          <label className="setting-label">
            <span>Review Words Per Day</span>
            <span className="setting-description">Maximum words to review daily</span>
          </label>
          <div className="setting-control">
            <input
              type="range"
              min="5"
              max="50"
              value={settings.maxReviewWords}
              onChange={(e) => handleSettingChange('maxReviewWords', parseInt(e.target.value))}
              className="setting-slider"
            />
            <span className="setting-value">{settings.maxReviewWords}</span>
          </div>
        </div>

        <div className="setting-group">
          <label className="setting-label">
            <span>Focus Period (Days)</span>
            <span className="setting-description">Days to focus on new words before introducing more</span>
          </label>
          <div className="setting-control">
            <input
              type="range"
              min="3"
              max="14"
              value={settings.focusPeriodDays}
              onChange={(e) => handleSettingChange('focusPeriodDays', parseInt(e.target.value))}
              className="setting-slider"
            />
            <span className="setting-value">{settings.focusPeriodDays}</span>
          </div>
        </div>
      </div>

      {/* Practice Settings */}
      <div className="settings-section">
        <h2>🎯 Practice Session</h2>
        
        <div className="setting-group">
          <label className="setting-label">
            <span>Session Size</span>
            <span className="setting-description">Number of words per practice session</span>
          </label>
          <div className="setting-control">
            <input
              type="range"
              min="5"
              max="30"
              value={settings.sessionSize}
              onChange={(e) => handleSettingChange('sessionSize', parseInt(e.target.value))}
              className="setting-slider"
            />
            <span className="setting-value">{settings.sessionSize}</span>
          </div>
        </div>

        <div className="setting-group">
          <label className="setting-label">
            <span>Auto-Advance</span>
            <span className="setting-description">Automatically move to next word after rating 1-4</span>
          </label>
          <div className="setting-control">
            <label className="toggle-switch">
              <input
                type="checkbox"
                checked={settings.autoAdvance}
                onChange={(e) => handleSettingChange('autoAdvance', e.target.checked)}
              />
              <span className="toggle-slider"></span>
            </label>
          </div>
        </div>

        <div className="setting-group">
          <label className="setting-label">
            <span>Keyboard Hints</span>
            <span className="setting-description">Show number keys on rating buttons</span>
          </label>
          <div className="setting-control">
            <label className="toggle-switch">
              <input
                type="checkbox"
                checked={settings.showKeyboardHints}
                onChange={(e) => handleSettingChange('showKeyboardHints', e.target.checked)}
              />
              <span className="toggle-slider"></span>
            </label>
          </div>
        </div>
      </div>

      {/* Learning Schedule Explanation */}
      <div className="settings-section">
        <h2>📅 Review Schedule</h2>
        <div className="schedule-explanation">
          <div className="schedule-item">
            <span className="level-badge level-0">0</span>
            <span className="schedule-text">Don't Know → Review tomorrow</span>
          </div>
          <div className="schedule-item">
            <span className="level-badge level-1">1</span>
            <span className="schedule-text">Beginner → Review in 2 days</span>
          </div>
          <div className="schedule-item">
            <span className="level-badge level-2">2</span>
            <span className="schedule-text">Learning → Review in 4 days</span>
          </div>
          <div className="schedule-item">
            <span className="level-badge level-3">3</span>
            <span className="schedule-text">Good → Review in 1 week</span>
          </div>
          <div className="schedule-item">
            <span className="level-badge level-4">4</span>
            <span className="schedule-text">Great → Review in 2 weeks</span>
          </div>
          <div className="schedule-item">
            <span className="level-badge level-5">5</span>
            <span className="schedule-text">Mastered → Review in 1 month (then exponential)</span>
          </div>
        </div>
      </div>

      {/* Action Buttons */}
      <div className="settings-actions">
        <button 
          className="reset-btn"
          onClick={resetToDefaults}
        >
          🔄 Reset to Defaults
        </button>
        
        <button 
          className={`save-btn ${saved ? 'saved' : ''}`}
          onClick={saveSettings}
          disabled={loading}
        >
          {loading ? '⏳ Saving...' : saved ? '✅ Saved!' : '💾 Save Settings'}
        </button>
      </div>
    </div>
  );
};

export default Settings;
