import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import axios from 'axios';
import WordList from './components/WordList';
import Practice from './components/Practice';
import Statistics from './components/Statistics';
import AdminImport from './components/AdminImport';
import Settings from './components/Settings';
import Auth from './components/Auth';
import UserPreferences from './components/UserPreferences';
import './App.css';

function App() {
  const [user, setUser] = useState(null);
  const [words, setWords] = useState([]);
  const [languagePairs, setLanguagePairs] = useState([]);
  const [selectedLanguagePair, setSelectedLanguagePair] = useState(null);
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState(null);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  useEffect(() => {
    checkAuthStatus();
  }, []);

  useEffect(() => {
    if (user) {
      fetchLanguagePairs();
      initializeUserData();
    }
  }, [user]);

  useEffect(() => {
    if (selectedLanguagePair && user) {
      fetchWords();
      fetchStats();
    }
  }, [selectedLanguagePair, user]);

  const fetchWords = async () => {
    try {
      const response = await axios.get('/api/words');
      setWords(response.data);
    } catch (error) {
      console.error('Error fetching words:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchStats = async () => {
    try {
      const response = await axios.get('/api/words/progress/stats');
      setStats(response.data);
    } catch (error) {
      console.error('Error fetching stats:', error);
    }
  };

  const handleWordAdded = (newWord) => {
    setWords([...words, newWord]);
    fetchStats();
  };

  const handleWordUpdated = (updatedWord) => {
    setWords(words.map(word => word.id === updatedWord.id ? updatedWord : word));
    fetchStats();
  };

  const handleWordDeleted = (wordId) => {
    setWords(words.filter(word => word.id !== wordId));
    fetchStats();
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const closeMobileMenu = () => {
    setIsMobileMenuOpen(false);
  };

  if (loading) {
    return (
      <div className="container">
        <div className="loading">Loading...</div>
      </div>
    );
  }

  return (
    <Router>
      <div className="App">
        <nav className="navbar">
          <div className="container">
            <h1 className="nav-title">Word Memory App</h1>
            
            {/* Mobile menu button */}
            <button 
              className="mobile-menu-btn"
              onClick={toggleMobileMenu}
              aria-label="Toggle navigation menu"
            >
              <span className={`hamburger ${isMobileMenuOpen ? 'open' : ''}`}>
                <span></span>
                <span></span>
                <span></span>
              </span>
            </button>
            
            {/* Navigation links */}
            <div className={`nav-links ${isMobileMenuOpen ? 'mobile-open' : ''}`}>
              <Link to="/" className="nav-link" onClick={closeMobileMenu}>Words</Link>
              <Link to="/practice" className="nav-link" onClick={closeMobileMenu}>Practice</Link>
              <Link to="/stats" className="nav-link" onClick={closeMobileMenu}>Statistics</Link>
              <Link to="/settings" className="nav-link" onClick={closeMobileMenu}>Settings</Link>
            </div>
            
            {/* Mobile menu overlay */}
            {isMobileMenuOpen && (
              <div className="mobile-menu-overlay" onClick={closeMobileMenu}></div>
            )}
          </div>
        </nav>

        <main className="main-content">
          <div className="container">
            <Routes>
              <Route 
                path="/" 
                element={
                  <WordList 
                    words={words} 
                    onWordUpdated={handleWordUpdated}
                    onWordDeleted={handleWordDeleted}
                  />
                } 
              />

              <Route 
                path="/practice" 
                element={
                  <Practice 
                    words={words}
                    onWordUpdated={handleWordUpdated}
                  />
                } 
              />
              <Route 
                path="/stats" 
                element={
                  <Statistics 
                    stats={stats}
                    words={words}
                  />
                } 
              />
              <Route 
                path="/admin" 
                element={<AdminImport />} 
              />
              <Route 
                path="/settings" 
                element={<Settings />} 
              />
            </Routes>
          </div>
        </main>
      </div>
    </Router>
  );
}

export default App;
