import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Settings.css';

const Settings = () => {
  const [settings, setSettings] = useState({
    maxFocusWords: 5,
    focusPeriodDays: 5,
    reviewIntervalDays: 7,
    masteredReviewDays: 30
  });
  
  const [stats, setStats] = useState({
    totalWords: 0,
    newWords: 0,
    learningWords: 0,
    masteredWords: 0,
    currentFocusWords: 0,
    reviewDue: 0
  });
  
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  useEffect(() => {
    loadStats();
    loadSettings();
  }, []);

  const loadStats = async () => {
    try {
      const response = await axios.get('/api/words/algorithm-stats');
      setStats(response.data);
    } catch (error) {
      console.error('Error loading stats:', error);
    }
  };

  const loadSettings = () => {
    const savedSettings = localStorage.getItem('spacedRepetitionSettings');
    if (savedSettings) {
      setSettings(JSON.parse(savedSettings));
    }
  };

  const saveSettings = () => {
    setLoading(true);
    try {
      localStorage.setItem('spacedRepetitionSettings', JSON.stringify(settings));
      setMessage('Settings saved successfully!');
      setTimeout(() => setMessage(''), 3000);
    } catch (error) {
      setMessage('Error saving settings');
      console.error('Error saving settings:', error);
    } finally {
      setLoading(false);
    }
  };

  const resetToDefaults = () => {
    const defaultSettings = {
      maxFocusWords: 5,
      focusPeriodDays: 5,
      reviewIntervalDays: 7,
      masteredReviewDays: 30
    };
    setSettings(defaultSettings);
    setMessage('Settings reset to defaults');
    setTimeout(() => setMessage(''), 3000);
  };

  const handleInputChange = (key, value) => {
    const numValue = parseInt(value);
    if (numValue > 0) {
      setSettings(prev => ({ ...prev, [key]: numValue }));
    }
  };

  return (
    <div className="settings-container">
      <div className="settings-header">
        <h1>Learning Algorithm Settings</h1>
        <p>Configure your spaced repetition learning experience</p>
      </div>

      {/* Algorithm Statistics */}
      <div className="stats-section">
        <h2>📊 Learning Statistics</h2>
        <div className="stats-grid">
          <div className="stat-card">
            <div className="stat-number">{stats.totalWords}</div>
            <div className="stat-label">Total Words</div>
          </div>
          <div className="stat-card new">
            <div className="stat-number">{stats.newWords}</div>
            <div className="stat-label">New Words</div>
          </div>
          <div className="stat-card learning">
            <div className="stat-number">{stats.learningWords}</div>
            <div className="stat-label">Learning</div>
          </div>
          <div className="stat-card mastered">
            <div className="stat-number">{stats.masteredWords}</div>
            <div className="stat-label">Mastered</div>
          </div>
          <div className="stat-card focus">
            <div className="stat-number">{stats.currentFocusWords}</div>
            <div className="stat-label">Focus Words</div>
          </div>
          <div className="stat-card review">
            <div className="stat-number">{stats.reviewDue}</div>
            <div className="stat-label">Review Due</div>
          </div>
        </div>
      </div>

      {/* Settings Configuration */}
      <div className="settings-section">
        <h2>⚙️ Algorithm Configuration</h2>
        
        <div className="setting-group">
          <label className="setting-label">
            <span className="label-text">
              <strong>Max Focus Words</strong>
              <small>How many words to focus on at once (1-10)</small>
            </span>
            <input
              type="number"
              min="1"
              max="10"
              value={settings.maxFocusWords}
              onChange={(e) => handleInputChange('maxFocusWords', e.target.value)}
              className="setting-input"
            />
          </label>
        </div>

        <div className="setting-group">
          <label className="setting-label">
            <span className="label-text">
              <strong>Focus Period (Days)</strong>
              <small>How long to focus on each word before moving on (1-14)</small>
            </span>
            <input
              type="number"
              min="1"
              max="14"
              value={settings.focusPeriodDays}
              onChange={(e) => handleInputChange('focusPeriodDays', e.target.value)}
              className="setting-input"
            />
          </label>
        </div>

        <div className="setting-group">
          <label className="setting-label">
            <span className="label-text">
              <strong>Review Interval (Days)</strong>
              <small>Days between reviews for learning words (1-30)</small>
            </span>
            <input
              type="number"
              min="1"
              max="30"
              value={settings.reviewIntervalDays}
              onChange={(e) => handleInputChange('reviewIntervalDays', e.target.value)}
              className="setting-input"
            />
          </label>
        </div>

        <div className="setting-group">
          <label className="setting-label">
            <span className="label-text">
              <strong>Mastered Review (Days)</strong>
              <small>Days between reviews for mastered words (7-365)</small>
            </span>
            <input
              type="number"
              min="7"
              max="365"
              value={settings.masteredReviewDays}
              onChange={(e) => handleInputChange('masteredReviewDays', e.target.value)}
              className="setting-input"
            />
          </label>
        </div>
      </div>

      {/* How It Works */}
      <div className="settings-section">
        <h2>🧠 How the Algorithm Works</h2>
        <div className="algorithm-explanation">
          <div className="explanation-card">
            <h3>🎯 Focus Period</h3>
            <p>The algorithm selects <strong>{settings.maxFocusWords} words</strong> to focus on for <strong>{settings.focusPeriodDays} days</strong>. You'll see these words repeatedly until you master them or the focus period ends.</p>
          </div>
          
          <div className="explanation-card">
            <h3>🔄 Review Cycles</h3>
            <p>Learning words (levels 3-4) are reviewed every <strong>{settings.reviewIntervalDays} days</strong> to reinforce memory.</p>
          </div>
          
          <div className="explanation-card">
            <h3>✅ Mastered Words</h3>
            <p>Mastered words (level 5) are reviewed every <strong>{settings.masteredReviewDays} days</strong> to prevent forgetting.</p>
          </div>
          
          <div className="explanation-card">
            <h3>📈 Smart Selection</h3>
            <p>New focus words are chosen based on your progress - prioritizing words you know less and haven't practiced recently.</p>
          </div>
        </div>
      </div>

      {/* Action Buttons */}
      <div className="settings-actions">
        <button 
          onClick={saveSettings}
          disabled={loading}
          className="save-btn"
        >
          {loading ? 'Saving...' : 'Save Settings'}
        </button>
        
        <button 
          onClick={resetToDefaults}
          className="reset-btn"
        >
          Reset to Defaults
        </button>
        
        <button 
          onClick={loadStats}
          className="refresh-btn"
        >
          Refresh Stats
        </button>
      </div>

      {message && (
        <div className={`message ${message.includes('Error') ? 'error' : 'success'}`}>
          {message}
        </div>
      )}
    </div>
  );
};

export default Settings;
