import React, { useState } from 'react';
import axios from 'axios';
import './WordList.css';

const WordList = ({ words, onWordUpdated, onWordDeleted }) => {
  const [filterText, setFilterText] = useState('');
  const [filterLevel, setFilterLevel] = useState('');
  const [editingWord, setEditingWord] = useState(null);
  const [editForm, setEditForm] = useState({ english: '', hebrew: '', difficulty: 1 });

  const handleEdit = (word) => {
    setEditingWord(word.id);
    setEditForm({
      english: word.english,
      hebrew: word.hebrew,
      userLevel: word.userLevel,
      difficulty: word.difficulty
    });
  };

  const handleSave = async (wordId) => {
    try {
      const response = await axios.put(`/api/words/${wordId}`, editForm);
      onWordUpdated(response.data);
      setEditingWord(null);
      setEditForm({});
    } catch (error) {
      console.error('Error updating word:', error);
      alert('Failed to update word');
    }
  };

  const handleCancel = () => {
    setEditingWord(null);
    setEditForm({});
  };

  const handleDelete = async (wordId) => {
    if (window.confirm('Are you sure you want to delete this word?')) {
      try {
        await axios.delete(`/api/words/${wordId}`);
        onWordDeleted(wordId);
      } catch (error) {
        console.error('Error deleting word:', error);
        alert('Failed to delete word');
      }
    }
  };

  const updateProgress = async (wordId, newLevel) => {
    try {
      const response = await axios.post(`/api/words/${wordId}/progress`, {
        userLevel: newLevel
      });
      onWordUpdated(response.data);
    } catch (error) {
      console.error('Error updating progress:', error);
    }
  };

  const reassessDifficulty = async (word) => {
    try {
      const response = await axios.post('/api/ai/assess-difficulty', {
        word: word.english,
        context: `Hebrew translation: ${word.hebrew}`
      });
      const newDifficulty = response.data.difficulty;
      const updateResponse = await axios.put(`/api/words/${word.id}`, { difficulty: newDifficulty });
      onWordUpdated(updateResponse.data);
      alert(`Difficulty reassessed: Level ${newDifficulty}`);
    } catch (error) {
      console.error('Error reassessing difficulty:', error);
      alert('Failed to reassess difficulty');
    }
  };

  const regenerateSentence = async (word) => {
    try {
      const response = await axios.post('/api/ai/generate-sentence', {
        word: word.english
      });
      const newSentence = response.data.sentence;
      const updateResponse = await axios.put(`/api/words/${word.id}`, { exampleSentence: newSentence });
      onWordUpdated(updateResponse.data);
      alert(`New example sentence generated:\n\n"${newSentence}"`);
    } catch (error) {
      console.error('Error regenerating sentence:', error);
      alert('Failed to regenerate sentence');
    }
  };

  const handleGenerateAllSentences = async () => {
    const confirmMessage = `This will generate example sentences for all words that don't have them.\n\nThis may take a few minutes. Continue?`;
    const userConfirmed = window.confirm(confirmMessage);

    if (userConfirmed) {
      try {
        const response = await axios.post('/api/words/generate-sentences');
        const details = response.data.details;
        alert(`‚úÖ Successfully generated sentences!\n\nüìä Results:\n‚Ä¢ Processed: ${details.processed} words\n‚Ä¢ Updated: ${details.updated} words\n‚Ä¢ Errors: ${details.errors} words\n\nüìù All sentences were generated using high-quality educational templates matched to difficulty levels.`);
        // Refresh the page to show new sentences
        window.location.reload();
      } catch (error) {
        console.error('Error generating sentences:', error);
        alert('‚ùå Failed to generate sentences: ' + (error.response?.data?.error || error.message));
      }
    }
  };

  const handleDeleteAll = async () => {
    const confirmMessage = `Are you sure you want to delete ALL ${words.length} words?\n\nThis action cannot be undone!`;
    const userConfirmed = window.confirm(confirmMessage);

    if (userConfirmed) {
      const finalConfirm = window.confirm('Final confirmation: This will permanently delete all words. Type "DELETE" to confirm.');
      if (finalConfirm) {
        try {
          const response = await axios.post('/api/words/clear-all', { confirm: true });
          alert(`Successfully deleted ${response.data.deletedCount} words`);
          // Refresh the page or clear the words list
          window.location.reload();
        } catch (error) {
          console.error('Error deleting all words:', error);
          alert('Failed to delete all words: ' + (error.response?.data?.error || error.message));
        }
      }
    }
  };

  const getLevelColor = (level) => {
    const colors = ['#dc3545', '#fd7e14', '#ffc107', '#20c997', '#198754', '#0d6efd'];
    return colors[Math.min(level, 5)];
  };

  const getDifficultyColor = (difficulty) => {
    const colors = ['', '#28a745', '#ffc107', '#fd7e14', '#dc3545', '#6f42c1'];
    return colors[difficulty];
  };

  // Filter words based on search text and level
  const filteredWords = words.filter(word => {
    const matchesText = word.english.toLowerCase().includes(filterText.toLowerCase()) ||
                       word.hebrew.includes(filterText);
    const matchesLevel = filterLevel === '' || word.userLevel.toString() === filterLevel;
    return matchesText && matchesLevel;
  });

  return (
    <div className="word-list">
      <div className="word-list-header">
        <h2>Word Collection ({words.length} words)</h2>
        <div className="header-controls">
          <div className="filter-controls">
            <input
              type="text"
              placeholder="Filter words..."
              value={filterText}
              onChange={(e) => setFilterText(e.target.value)}
              className="filter-input"
            />
            <select
              value={filterLevel}
              onChange={(e) => setFilterLevel(e.target.value)}
              className="filter-select"
            >
              <option value="">All Levels</option>
              <option value="0">Level 0</option>
              <option value="1">Level 1</option>
              <option value="2">Level 2</option>
              <option value="3">Level 3</option>
              <option value="4">Level 4</option>
              <option value="5">Level 5</option>
            </select>
          </div>

          <div className="action-controls">
            <button className="btn btn-success generate-sentences-btn" onClick={handleGenerateAllSentences} title="Generate example sentences for all words">üìù Generate All Sentences</button>
            <button className="btn btn-danger delete-all-btn" onClick={handleDeleteAll} title="Delete all words (Admin only)">üóëÔ∏è Delete All Words</button>
          </div>
        </div>
      </div>

      {filteredWords.length === 0 ? (
        <div className="no-words">
          <p>No words found with the current filter.</p>
        </div>
      ) : (
        <div className="words-grid">
          {filteredWords.map(word => (
            <div key={word.id} className="word-card">
              {editingWord === word.id ? (
                <div className="edit-form">
                  <div className="form-group">
                    <label>English:</label>
                    <input
                      type="text"
                      value={editForm.english}
                      onChange={(e) => setEditForm({...editForm, english: e.target.value})}
                    />
                  </div>
                  <div className="form-group">
                    <label>Hebrew:</label>
                    <input
                      type="text"
                      value={editForm.hebrew}
                      onChange={(e) => setEditForm({...editForm, hebrew: e.target.value})}
                    />
                  </div>
                  <div className="form-group">
                    <label>Difficulty:</label>
                    <select
                      value={editForm.difficulty}
                      onChange={(e) => setEditForm({...editForm, difficulty: parseInt(e.target.value)})}
                    >
                      {[1,2,3,4,5].map(level => (
                        <option key={level} value={level}>Level {level}</option>
                      ))}
                    </select>
                  </div>
                  <div className="edit-actions">
                    <button className="btn btn-primary" onClick={() => handleSave(word.id)}>
                      Save
                    </button>
                    <button className="btn btn-secondary" onClick={handleCancel}>
                      Cancel
                    </button>
                  </div>
                </div>
              ) : (
                <>
                  <div className="word-content">
                    <div className="word-english">{word.english}</div>
                    <div className="word-hebrew">{word.hebrew}</div>
                    {word.exampleSentence && (
                      <div className="example-sentence">
                        <strong>Example:</strong> {word.exampleSentence}
                      </div>
                    )}
                    <div className="word-meta">
                      <span 
                        className="difficulty-badge"
                        style={{backgroundColor: getDifficultyColor(word.difficulty)}}
                      >
                        Difficulty: {word.difficulty}
                      </span>
                      <span 
                        className="level-badge"
                        style={{backgroundColor: getLevelColor(word.userLevel)}}
                      >
                        Level: {word.userLevel}
                      </span>
                    </div>
                  </div>

                  <div className="word-controls">
                    <button className="btn btn-primary btn-sm" onClick={() => handleEdit(word)} title="Edit word">‚úèÔ∏è</button>
                    <button className="btn btn-danger btn-sm" onClick={() => handleDelete(word.id)} title="Delete word">üóëÔ∏è</button>
                    <button className="btn btn-info btn-sm" onClick={() => reassessDifficulty(word)} title="Reassess difficulty with AI">üîÑ AI</button>
                    <button className="btn btn-success btn-sm" onClick={() => regenerateSentence(word)} title="Regenerate example sentence">üìù</button>
                  </div>
                </>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default WordList;
