import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Practice.css';

const Practice = ({ words, onWordUpdated }) => {
  const [currentMode, setCurrentMode] = useState(null); // null = main menu, 'knowledge', 'flashcards'

  const handleModeSelect = (mode) => {
    setCurrentMode(mode);
  };

  const handleExit = () => {
    setCurrentMode(null);
  };

  // Main menu
  if (currentMode === null) {
    return (
      <div className="practice-main-menu">
        <div className="practice-menu-container">
          <h1>Practice Mode</h1>
          <div className="practice-options">
            <button 
              className="practice-option knowledge-check"
              onClick={() => handleModeSelect('knowledge')}
            >
              <div className="option-icon">üìä</div>
              <div className="option-content">
                <h3>Knowledge Check</h3>
                <p>Test your knowledge level with interactive quizzes</p>
              </div>
            </button>

            <button 
              className="practice-option flashcards"
              onClick={() => handleModeSelect('flashcards')}
            >
              <div className="option-icon">üÉè</div>
              <div className="option-content">
                <h3>Flash Cards</h3>
                <p>Traditional flash card practice mode</p>
              </div>
            </button>
          </div>
        </div>
      </div>
    );
  }

  // Knowledge Check Mode
  if (currentMode === 'knowledge') {
    return <KnowledgeCheck words={words} onWordUpdated={onWordUpdated} onExit={handleExit} />;
  }

  // Flash Cards Mode
  if (currentMode === 'flashcards') {
    return <FlashCards words={words} onWordUpdated={onWordUpdated} onExit={handleExit} />;
  }

  return null;
};

// Knowledge Check Component
const KnowledgeCheck = ({ words, onWordUpdated, onExit }) => {
  const [currentWordIndex, setCurrentWordIndex] = useState(0);
  const [showHebrew, setShowHebrew] = useState(false);
  const [selectedLevel, setSelectedLevel] = useState(null);
  const [practiceWords, setPracticeWords] = useState([]);
  const [isUpdating, setIsUpdating] = useState(false);
  const [localWords, setLocalWords] = useState([]);
  const [isPlaying, setIsPlaying] = useState(false);
  const [speechSupported, setSpeechSupported] = useState(false);

  useEffect(() => {
    // Create a local copy of words to prevent issues
    setLocalWords([...words]);

    // Check if speech synthesis is supported
    if ('speechSynthesis' in window) {
      setSpeechSupported(true);
    }
  }, [words]);

  useEffect(() => {
    // Filter words for practice - include words that are not mastered (level < 5)
    const filtered = localWords.filter(word => word.userLevel < 5);
    setPracticeWords(filtered);

    // Reset state when words change
    if (filtered.length === 0) {
      setCurrentWordIndex(0);
      setShowHebrew(false);
      setSelectedLevel(null);
    } else if (currentWordIndex >= filtered.length) {
      // If current index is out of bounds, reset to 0
      setCurrentWordIndex(0);
      setShowHebrew(false);
      setSelectedLevel(null);
    }
  }, [localWords, currentWordIndex]);

  const currentWord = practiceWords[currentWordIndex];

  // Speech synthesis functions
  const speakWord = (word) => {
    if (!speechSupported || !word) return;

    // Stop any current speech
    window.speechSynthesis.cancel();

    setIsPlaying(true);

    const utterance = new SpeechSynthesisUtterance(word);
    utterance.lang = 'en-US';
    utterance.rate = 0.8; // Slightly slower for learning
    utterance.pitch = 1;
    utterance.volume = 1;

    utterance.onend = () => {
      setIsPlaying(false);
    };

    utterance.onerror = () => {
      setIsPlaying(false);
      console.error('Speech synthesis error');
    };

    window.speechSynthesis.speak(utterance);
  };

  const stopSpeech = () => {
    if (speechSupported) {
      window.speechSynthesis.cancel();
      setIsPlaying(false);
    }
  };

  // Auto-play word when it changes
  useEffect(() => {
    if (currentWord && speechSupported && !isUpdating) {
      // Small delay to ensure the component is ready
      setTimeout(() => {
        speakWord(currentWord.english);
      }, 500);
    }

    // Cleanup function to stop speech when component unmounts or word changes
    return () => {
      stopSpeech();
    };
  }, [currentWord, speechSupported, isUpdating]);

  const handleLevelSelect = async (level) => {
    if (!currentWord || isUpdating) {
      return;
    }

    // Double check that the word still exists in practice words
    const wordStillExists = practiceWords.find(w => w.id === currentWord.id);
    if (!wordStillExists) {
      alert('Word no longer available. Please refresh and try again.');
      return;
    }

    setIsUpdating(true);
    setSelectedLevel(level);

    try {
      const response = await axios.post(`/api/words/${currentWord.id}/progress`, {
        userLevel: level
      });

      // Update the word in the parent component
      onWordUpdated(response.data);

      // For level 5, we don't immediately remove the word from local list
      // Instead, we update it and let the useEffect handle the filtering
      setLocalWords(prev => 
        prev.map(word => 
          word.id === currentWord.id 
            ? { ...word, userLevel: level }
            : word
        )
      );

      // Wait a bit before moving to next word
      setTimeout(() => {
        if (currentWordIndex < practiceWords.length - 1) {
          setCurrentWordIndex(prev => prev + 1);
          setShowHebrew(false);
          setSelectedLevel(null);
        } else {
          alert('Knowledge check completed!');
          onExit();
        }
        setIsUpdating(false);
      }, 1500);
    } catch (error) {
      console.error('Error updating progress:', error);
      setSelectedLevel(null);
      setIsUpdating(false);
      alert('Failed to update progress. Please try again.');
    }
  };

  const getLevelColor = (level) => {
    const colors = ['#dc3545', '#fd7e14', '#ffc107', '#20c997', '#198754', '#0d6efd'];
    return colors[Math.min(level, 5)];
  };

  if (practiceWords.length === 0) {
    return (
      <div className="practice-fullscreen">
        <button className="exit-button" onClick={onExit}>‚úï</button>
        <div className="practice-content">
          <div className="no-words">
            <h2>No words available for practice</h2>
            <p>All your words are already mastered (Level 5) or you need to add more words.</p>
            <button onClick={onExit}>Back to Menu</button>
          </div>
        </div>
      </div>
    );
  }

  if (!currentWord) {
    return (
      <div className="practice-fullscreen">
        <button className="exit-button" onClick={onExit}>‚úï</button>
        <div className="practice-content">
          <div className="no-words">
            <h2>No words available for practice</h2>
            <button onClick={onExit}>Back to Menu</button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="practice-fullscreen">
      <button className="exit-button" onClick={onExit}>‚úï</button>
      <div className="practice-content">
        <div className="knowledge-check-container">
          <div className="progress-info">
            <span>Word {currentWordIndex + 1} of {practiceWords.length}</span>
          </div>

          <div className="word-display">
            <h2 className="english-word">{currentWord.english}</h2>
            {showHebrew && (
              <h3 className="hebrew-word">{currentWord.hebrew}</h3>
            )}

            {speechSupported && (
              <div className="voice-controls">
                <button 
                  className={`voice-btn ${isPlaying ? 'playing' : ''}`}
                  onClick={() => speakWord(currentWord.english)}
                  disabled={isPlaying}
                  title="Play pronunciation"
                >
                  {isPlaying ? 'üîä Playing...' : 'üîä Play'}
                </button>

                {isPlaying && (
                  <button 
                    className="voice-btn stop-btn"
                    onClick={stopSpeech}
                    title="Stop pronunciation"
                  >
                    ‚èπÔ∏è Stop
                  </button>
                )}
              </div>
            )}
          </div>

          <div className="knowledge-buttons">
            <button 
              className={`level-btn ${selectedLevel === 0 ? 'selected' : ''}`}
              onClick={() => handleLevelSelect(0)}
              disabled={selectedLevel !== null}
            >
              <span className="level-number">0</span>
              <span className="level-label">Don't Know</span>
            </button>

            <button 
              className={`level-btn ${selectedLevel === 1 ? 'selected' : ''}`}
              onClick={() => handleLevelSelect(1)}
              disabled={selectedLevel !== null}
            >
              <span className="level-number">1</span>
              <span className="level-label">Beginner</span>
            </button>

            <button 
              className={`level-btn ${selectedLevel === 2 ? 'selected' : ''}`}
              onClick={() => handleLevelSelect(2)}
              disabled={selectedLevel !== null}
            >
              <span className="level-number">2</span>
              <span className="level-label">Basic</span>
            </button>

            <button 
              className={`level-btn ${selectedLevel === 3 ? 'selected' : ''}`}
              onClick={() => handleLevelSelect(3)}
              disabled={selectedLevel !== null}
            >
              <span className="level-number">3</span>
              <span className="level-label">Intermediate</span>
            </button>

            <button 
              className={`level-btn ${selectedLevel === 4 ? 'selected' : ''}`}
              onClick={() => handleLevelSelect(4)}
              disabled={selectedLevel !== null}
            >
              <span className="level-number">4</span>
              <span className="level-label">Advanced</span>
            </button>

            <button 
              className={`level-btn ${selectedLevel === 5 ? 'selected' : ''}`}
              onClick={() => handleLevelSelect(5)}
              disabled={selectedLevel !== null}
            >
              <span className="level-number">5</span>
              <span className="level-label">Mastered</span>
            </button>
          </div>

          <div className="show-hebrew-section">
            <button 
              className="show-hebrew-btn"
              onClick={() => setShowHebrew(!showHebrew)}
            >
              {showHebrew ? 'Hide Hebrew' : 'Show Hebrew'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

// Flash Cards Component
const FlashCards = ({ words, onWordUpdated, onExit }) => {
  const [currentWordIndex, setCurrentWordIndex] = useState(0);
  const [showAnswer, setShowAnswer] = useState(false);
  const [practiceWords, setPracticeWords] = useState([]);
  const [practiceMode, setPracticeMode] = useState('all');

  useEffect(() => {
    let filtered = [];
    switch (practiceMode) {
      case 'new':
        filtered = words.filter(word => word.userLevel === 0);
        break;
      case 'learning':
        filtered = words.filter(word => word.userLevel > 0 && word.userLevel < 5);
        break;
      case 'review':
        filtered = words.filter(word => word.userLevel > 0);
        break;
      default:
        filtered = words;
    }
    setPracticeWords(filtered);
    setCurrentWordIndex(0);
    setShowAnswer(false);
  }, [words, practiceMode]);

  const currentWord = practiceWords[currentWordIndex];

  const handleNext = () => {
    if (currentWordIndex < practiceWords.length - 1) {
      setCurrentWordIndex(currentWordIndex + 1);
      setShowAnswer(false);
    }
  };

  const handlePrevious = () => {
    if (currentWordIndex > 0) {
      setCurrentWordIndex(currentWordIndex - 1);
      setShowAnswer(false);
    }
  };

  const handleProgressUpdate = async (newLevel) => {
    if (!currentWord) return;

    try {
      const response = await axios.post(`/api/words/${currentWord.id}/progress`, {
        userLevel: newLevel
      });
      onWordUpdated(response.data);
    } catch (error) {
      console.error('Error updating progress:', error);
    }
  };

  const getLevelColor = (level) => {
    const colors = ['#dc3545', '#fd7e14', '#ffc107', '#20c997', '#198754', '#0d6efd'];
    return colors[Math.min(level, 5)];
  };

  if (practiceWords.length === 0) {
    return (
      <div className="practice-fullscreen">
        <button className="exit-button" onClick={onExit}>‚úï</button>
        <div className="practice-content">
          <div className="no-words">
            <h2>No words available for practice</h2>
            <button onClick={onExit}>Back to Menu</button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="practice-fullscreen">
      <button className="exit-button" onClick={onExit}>‚úï</button>
      <div className="practice-content">
        <div className="flashcard-container">
          <div className="flashcard-header">
            <select 
              value={practiceMode} 
              onChange={(e) => setPracticeMode(e.target.value)}
              className="practice-mode-select"
            >
              <option value="all">All Words</option>
              <option value="new">New Words (Level 0)</option>
              <option value="learning">Learning (Level 1-4)</option>
              <option value="review">Review (Level 1-5)</option>
            </select>

            <div className="flashcard-progress">
              {currentWordIndex + 1} / {practiceWords.length}
            </div>
          </div>

          <div className="flashcard">
            <div className="flashcard-content">
              <h2 className="flashcard-word">{currentWord.english}</h2>
              {showAnswer && (
                <div className="flashcard-answer">
                  <h3>{currentWord.hebrew}</h3>
                  <div className="word-meta">
                    <span 
                      className="difficulty-badge"
                      style={{backgroundColor: getLevelColor(currentWord.difficulty)}}
                    >
                      Difficulty: {currentWord.difficulty}
                    </span>
                    <span 
                      className="level-badge"
                      style={{backgroundColor: getLevelColor(currentWord.userLevel)}}
                    >
                      Your Level: {currentWord.userLevel}
                    </span>
                  </div>
                </div>
              )}
            </div>
          </div>

          <div className="flashcard-controls">
            <button onClick={handlePrevious} disabled={currentWordIndex === 0}>
              ‚Üê Previous
            </button>

            <button 
              className="show-answer-btn"
              onClick={() => setShowAnswer(!showAnswer)}
            >
              {showAnswer ? 'Hide Answer' : 'Show Answer'}
            </button>

            <button onClick={handleNext} disabled={currentWordIndex === practiceWords.length - 1}>
              Next ‚Üí
            </button>
          </div>

          {showAnswer && (
            <div className="progress-controls">
              <button 
                onClick={() => handleProgressUpdate(Math.max(0, currentWord.userLevel - 1))}
                disabled={currentWord.userLevel === 0}
              >
                Decrease Level
              </button>
              <span>Current Level: {currentWord.userLevel}</span>
              <button 
                onClick={() => handleProgressUpdate(Math.min(5, currentWord.userLevel + 1))}
                disabled={currentWord.userLevel === 5}
              >
                Increase Level
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Practice;
